CREATE TABLE tweet (id BIGINT AUTO_INCREMENT, user_id BIGINT NOT NULL, tweet_source_id BIGINT NOT NULL, geo_id BIGINT, contributors VARCHAR(255), contributors_enabled TINYINT(1), in_reply_to_user_id BIGINT, in_reply_to_status_id VARCHAR(20), tweet_created_at DATETIME NOT NULL, truncated TINYINT(1) DEFAULT '0' NOT NULL, tweet_twitter_id VARCHAR(20), source VARCHAR(255), text VARCHAR(140) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), INDEX tweet_source_id_idx (tweet_source_id), INDEX geo_id_idx (geo_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE tweet_geo_location (id BIGINT AUTO_INCREMENT, latitude FLOAT(9, 6) DEFAULT 0 NOT NULL, longitude FLOAT(9, 6) DEFAULT 0 NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE tweet_source (id BIGINT AUTO_INCREMENT, label VARCHAR(255) DEFAULT 'web' NOT NULL, url VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE tweet_user (id BIGINT AUTO_INCREMENT, screen_name VARCHAR(15) NOT NULL, description VARCHAR(255), followers_count BIGINT DEFAULT 0 NOT NULL, contributors_enabled TINYINT(1) DEFAULT '0' NOT NULL, url VARCHAR(255), friends_count BIGINT DEFAULT 0 NOT NULL, geo_enabled TINYINT(1), twitter_created_at DATETIME NOT NULL, time_zone VARCHAR(255) NOT NULL, location VARCHAR(255), name VARCHAR(255), lang VARCHAR(10), twitter_user_id BIGINT NOT NULL, utc_offset BIGINT, profile_image_url VARCHAR(255) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
ALTER TABLE tweet ADD CONSTRAINT tweet_user_id_tweet_user_id FOREIGN KEY (user_id) REFERENCES tweet_user(id) ON DELETE CASCADE;
ALTER TABLE tweet ADD CONSTRAINT tweet_tweet_source_id_tweet_source_id FOREIGN KEY (tweet_source_id) REFERENCES tweet_source(id) ON DELETE CASCADE;
ALTER TABLE tweet ADD CONSTRAINT tweet_geo_id_tweet_geo_location_id FOREIGN KEY (geo_id) REFERENCES tweet_geo_location(id) ON DELETE CASCADE;
