CREATE TABLE tweet (id BIGINT AUTO_INCREMENT, user_id BIGINT NOT NULL, text VARCHAR(140) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE twitter_user (id BIGINT AUTO_INCREMENT, name VARCHAR(255) NOT NULL UNIQUE, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
ALTER TABLE tweet ADD CONSTRAINT tweet_user_id_twitter_user_id FOREIGN KEY (user_id) REFERENCES twitter_user(id) ON DELETE CASCADE;
